    private void bt_kembalianActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_kembalianActionPerformed
        // TODO add your handling code here:
        String bayarr = bayar.getText();
        int bayars=Integer.parseInt(bayarr);
        
        String totall = total.getText();
        int totals=Integer.parseInt(totall);
        
        int kembali = bayars-totals;
        
       kembalian.setText("" + kembali);
    }//GEN-LAST:event_bt_kembalianActionPerformed

