    private void bt_totalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_totalActionPerformed
        // TODO add your handling code here:
         int total1=0;
         
        if (rujak1.isSelected())
          {

            String Rujaks=BR.getText();

            int BR=Integer.parseInt(Rujaks);

            int harga_rujak=BR*5000;
            total1=total1+harga_rujak;
          }
        
         if (tahutek1.isSelected())
          {

            String teks=BTT.getText();

            int BTT=Integer.parseInt(teks);

            int harga_teks=BTT*9000;
            total1=total1+harga_teks;
          }
         
         if (tahulontong1.isSelected())
          {

            String lontong=BTL.getText();

            int BTL=Integer.parseInt(lontong);

            int harga_lontong=BTL*10000;
            total1=total1+harga_lontong;
          }
          
           if (blendrang1.isSelected())
          {

            String tewel=BBT.getText();

            int BBT=Integer.parseInt(tewel);

            int harga_tewel=BBT*7000;
            total1=total1+harga_tewel;
          }
        
         if (nasibantingan1.isSelected())
          {

            String bantingan=BNB.getText();

            int BNB=Integer.parseInt(bantingan);

            int harga_bantingan=BNB*10000;
            total1=total1+harga_bantingan;
          }
         
         if (tahugenjrot1.isSelected())
          {

            String genjrot=BTG.getText();

            int BTG=Integer.parseInt(genjrot);

            int harga_genjrot=BTG*7000;
            total1=total1+harga_genjrot;
          }
        
         if (kopi1.isSelected())
          {

            String kopis=MK.getText();

            int MK=Integer.parseInt(kopis);

            int harga_kopi=MK*3000;
            total1=total1+harga_kopi;
          }
         
          if (escaon1.isSelected())
          {

            String caon=MEC.getText();

            int MEC=Integer.parseInt(caon);

            int harga_caon=MEC*4000;
            total1=total1+harga_caon;
          }
          
          if (tehtawar1.isSelected())
          {

            String tawar=MTT.getText();

            int MTT=Integer.parseInt(tawar);

            int harga_tawar=MTT*2500;
            total1=total1+harga_tawar;
          }
          
         if (aqua1.isSelected())
          {

            String aquas=MA.getText();

            int MA=Integer.parseInt(aquas);

            int harga_aqua=MA*500;
            total1=total1+harga_aqua;
          }
         
         if (esoyen1.isSelected())
          {

            String oyen=MEO.getText();

            int MEO=Integer.parseInt(oyen);

            int harga_oyen=MEO*3000;
            total1=total1+harga_oyen;
          }
         
         if (espisangijo1.isSelected())
          {

            String ijo=MEPI.getText();

            int MEPI=Integer.parseInt(ijo);

            int harga_ijo=MEPI*6000;
            total1=total1+harga_ijo;
          }
          
          total.setText("" +total1); 
    }//GEN-LAST:event_bt_totalActionPerformed

